mapper.select:
SELECT DATE_FORMAT(A.STARTDATE, '%y%m%d') AS startDate, DATE_FORMAT(A.ENDDATE, '%y%m%d') AS endDate FROM (
SELECT CASE WHEN ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND ( #{searchParam.endDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d'))) THEN DATE_FORMAT(ON_DATE, '%Y-%m-%d')
WHEN ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) THEN DATE_FORMAT(ON_DATE, '%Y-%m-%d')
WHEN ( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} <= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') THEN #{searchParam.startDate}
WHEN ( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') THEN #{searchParam.startDate} END AS startDate
, CASE WHEN ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND ( #{searchParam.endDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d'))) THEN #{searchParam.endDate}
WHEN ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()) , '%Y-%m-%d') ) THEN DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d')
WHEN ( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} <= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') THEN #{searchParam.endDate}
WHEN ( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') THEN DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') END AS endDate FROM MEMBER_CAMPAIGN_HIS WHERE 1=1 AND MEMBER_ID = #{memberId} AND CAMPAIGN_ID = #{campaignId} AND ( ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND ( #{searchParam.endDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d'))) OR ( #{searchParam.startDate} <= DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()) , '%Y-%m-%d') ) OR (( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} <= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d')) OR (( #{searchParam.startDate} BETWEEN DATE_FORMAT(ON_DATE, '%Y-%m-%d') AND DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) AND #{searchParam.endDate} >= DATE_FORMAT(IFNULL(OFF_DATE, NOW()), '%Y-%m-%d') ) )
) A GROUP BY A.STARTDATE, A.ENDDATE
